import ReactDOM from "react-dom";
import React, { Component } from "react";
import { w3cwebsocket as w3WebSocket } from "websocket";
import { Input } from "antd";

import "./index.css";
import "antd/dist/antd.css";

const client = new w3WebSocket("ws://127.0.0.1:8001");

const { Search } = Input;

export default class App extends Component {
  state = {
    userName: "",
    isLoggedIn: false,
  };

  handleLogin = (value) => {
    this.setState({
      userName: value,
      isLoggedIn: true,
    });
  };

  handleSendMessage = (value) => {
    client.send(
      JSON.stringify({
        type: "message",
        message: value,
      })
    );
  };

  componentDidMount() {
    client.onopen = () => {
      console.log("websocket client connected ðŸ¤ ");
    };

    client.onclose = () => {
      console.log("websocket client connection close ðŸšª  ");
    };
  }

  render() {
    return (
      <div className="main">
        {this.state.isLoggedIn ? (
          <div> </div>
        ) : (
          <div className="login">
            <Search
              placeholder="Enter username"
              enterButton="Login"
              size="large"
              onSearch={(value) => handleLogin(value)}
            />
          </div>
        )}
      </div>
    );
  }
}

ReactDOM.render(<App />, document.getElementById("root"));
